// üìÑ PHOENIX CV GENERATOR - Version N8N Pure
// G√©n√©ration CV IA optimis√© pour reconversions professionnelles

const inputData = $input.first().json;
const startTime = Date.now();

// ‚úÖ VALIDATION S√âCURIS√âE
if (!inputData.user_email || !inputData.personal_info) {
  throw new Error('Donn√©es manquantes: user_email et personal_info requis');
}

if (!inputData.personal_info.first_name || !inputData.personal_info.last_name) {
  throw new Error('Nom et pr√©nom requis dans personal_info');
}

if (!inputData.personal_info.email) {
  throw new Error('Email requis dans personal_info');
}

// üéØ CONFIGURATION PHOENIX CV
const config = {
  user_email: inputData.user_email,
  user_tier: inputData.user_tier || 'free',
  template_id: inputData.template_id || 'modern',
  ats_optimization: inputData.ats_optimization || false,
  target_job: inputData.target_job || '',
  session_id: `phoenix_cv_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
};

// üìä STRUCTURATION DONN√âES CV
const cvProfile = {
  personal_info: {
    first_name: inputData.personal_info.first_name,
    last_name: inputData.personal_info.last_name,
    email: inputData.personal_info.email,
    phone: inputData.personal_info.phone || '',
    city: inputData.personal_info.city || '',
    linkedin_url: inputData.personal_info.linkedin_url || ''
  },
  professional_summary: inputData.professional_summary || '',
  experiences: inputData.experiences || [],
  education: inputData.education || [],
  skills: inputData.skills || [],
  languages: inputData.languages || [],
  projects: inputData.projects || []
};

// üé® G√âN√âRATION R√âSUM√â PROFESSIONNEL IA
let summaryPrompt = '';
if (!cvProfile.professional_summary && config.user_tier === 'premium') {
  summaryPrompt = buildProfessionalSummaryPrompt(cvProfile, config.target_job);
}

// üîç ANALYSE ATS SI PREMIUM
let atsPrompt = '';
if (config.ats_optimization && config.user_tier === 'premium' && inputData.job_description) {
  atsPrompt = buildATSOptimizationPrompt(cvProfile, inputData.job_description);
}

// üìã PR√âPARATION DONN√âES COMPL√àTES
const processingData = {
  config: config,
  cv_profile: cvProfile,
  summary_prompt: summaryPrompt,
  ats_prompt: atsPrompt,
  job_description: inputData.job_description || '',
  metadata: {
    generation_start: startTime,
    phoenix_version: '2.0.0',
    template_requested: config.template_id,
    features_requested: {
      ats_optimization: config.ats_optimization,
      ai_summary: !!summaryPrompt,
      premium_features: config.user_tier === 'premium'
    }
  }
};

return { json: processingData };

// üõ†Ô∏è FONCTIONS UTILITAIRES

function buildProfessionalSummaryPrompt(profile, targetJob) {
  const experiences = profile.experiences.slice(0, 3).map(exp => 
    `${exp.position} chez ${exp.company}`
  ).join(', ');

  const skills = profile.skills.slice(0, 8).map(skill => skill.name || skill).join(', ');

  return `Cr√©ez un r√©sum√© professionnel percutant pour ce profil en reconversion :

Exp√©riences : ${experiences}
Comp√©tences : ${skills}
Poste vis√© : ${targetJob || '√âvolution de carri√®re'}

Consignes :
- 3-4 lignes maximum
- Valoriser les comp√©tences transf√©rables
- Montrer la motivation pour la reconversion
- Ton professionnel et confiant

R√©sum√© professionnel :`;
}

function buildATSOptimizationPrompt(profile, jobDescription) {
  return `Analysez cette offre d'emploi et sugg√©rez des optimisations ATS pour ce CV :

Offre d'emploi :
${jobDescription.substring(0, 1000)}

Profil candidat :
- Exp√©riences : ${profile.experiences.length} postes
- Comp√©tences : ${profile.skills.map(s => s.name || s).join(', ')}

Fournissez :
1. Score ATS estim√© (/100)
2. 5 mots-cl√©s manquants √† ajouter
3. 3 recommandations d'am√©lioration

Format JSON uniquement.`;
}

// üìÑ PHOENIX CV - Assemblage Final & Template

const builderData = $('Phoenix CV Builder').first().json;
const generationTime = Date.now() - builderData.metadata.generation_start;

// üîç R√âCUP√âRATION DES DONN√âES IA
let aiSummary = '';
let atsAnalysis = null;

// R√©cup√©ration du r√©sum√© IA si g√©n√©r√©
try {
  const summaryNode = $('Generate AI Summary');
  if (summaryNode && summaryNode.length > 0) {
    const summaryResponse = summaryNode.first().json;
    aiSummary = summaryResponse.candidates[0]?.content?.parts[0]?.text || '';
    aiSummary = aiSummary.replace(/\*\*([^*]+)\*\*/g, '$1').trim();
  }
} catch (e) {
  // Pas de r√©sum√© IA g√©n√©r√©
}

// R√©cup√©ration de l'analyse ATS si g√©n√©r√©e
try {
  const atsNode = $('Generate ATS Analysis');
  if (atsNode && atsNode.length > 0) {
    const atsResponse = atsNode.first().json;
    const atsText = atsResponse.candidates[0]?.content?.parts[0]?.text || '';
    try {
      atsAnalysis = JSON.parse(atsText);
    } catch (e) {
      // Parsing JSON √©chou√©, analyse basique
      atsAnalysis = {
        score: 75,
        missing_keywords: [],
        recommendations: ['Optimisation ATS non disponible']
      };
    }
  }
} catch (e) {
  // Pas d'analyse ATS
}

// üé® CONSTRUCTION CV FINAL
const finalCVProfile = {
  ...builderData.cv_profile,
  professional_summary: aiSummary || builderData.cv_profile.professional_summary || generateDefaultSummary(builderData.cv_profile)
};

// üìä CALCUL SCORE GLOBAL
const qualityScore = calculateCVQuality(finalCVProfile, builderData.config);
const atsScore = atsAnalysis?.score || qualityScore;

// üé® G√âN√âRATION HTML (Template Engine)
const cvHTML = generateCVHTML(finalCVProfile, builderData.config.template_id);

// üå± CALCUL IMPACT ENVIRONNEMENTAL
const co2Impact = calculateCO2Impact(builderData.config.user_tier, generationTime, !!aiSummary, !!atsAnalysis);

// üí° RECOMMANDATIONS PREMIUM
const recommendations = generateRecommendations(finalCVProfile, atsAnalysis, builderData.config);

// üìÑ R√âSULTAT FINAL PHOENIX CV
const phoenixCVResult = {
  success: true,
  cv_id: `phoenix_cv_${Date.now()}_${Math.random().toString(36).substr(2, 8)}`,
  cv_html: cvHTML,
  cv_profile: finalCVProfile,
  user_email: builderData.config.user_email,
  user_tier: builderData.config.user_tier,
  template_id: builderData.config.template_id,
  generation_time_ms: generationTime,
  generation_time_s: Math.round(generationTime / 1000 * 100) / 100,
  quality_score: qualityScore,
  ats_score: atsScore,
  co2_impact_grams: co2Impact,
  recommendations: recommendations,
  premium_features_used: getPremiumFeaturesUsed(!!aiSummary, !!atsAnalysis),
  metadata: {
    template_used: builderData.config.template_id,
    ai_summary_generated: !!aiSummary,
    ats_analysis_performed: !!atsAnalysis,
    sections_count: countCVSections(finalCVProfile),
    total_experience_years: calculateTotalExperience(finalCVProfile.experiences),
    session_id: builderData.config.session_id,
    phoenix_version: '2.0.0',
    timestamp: new Date().toISOString()
  }
};

return { json: phoenixCVResult };

// üõ†Ô∏è FONCTIONS UTILITAIRES

function generateDefaultSummary(profile) {
  const firstName = profile.personal_info.first_name;
  const experienceCount = profile.experiences.length;
  const skillsCount = profile.skills.length;

  return `${firstName}, professionnel(le) exp√©riment√©(e) avec ${experienceCount} exp√©riences diversifi√©es et ${skillsCount} comp√©tences cl√©s. En transition professionnelle, je mets √† profit mon parcours atypique pour apporter une perspective unique et des comp√©tences transf√©rables solides.`;
}

function calculateCVQuality(profile, config) {
  let score = 0;

  // Sections compl√®tes (40 points)
  if (profile.personal_info.first_name && profile.personal_info.last_name) score += 10;
  if (profile.professional_summary && profile.professional_summary.length > 50) score += 15;
  if (profile.experiences.length > 0) score += 10;
  if (profile.skills.length >= 5) score += 5;

  // D√©tail des exp√©riences (30 points)
  profile.experiences.forEach(exp => {
    if (exp.description && exp.description.length > 50) score += 5;
  });
  score = Math.min(score, 70); // Max 70 pour cette section

  // Contact info (15 points)
  if (profile.personal_info.email) score += 7;
  if (profile.personal_info.phone) score += 4;
  if (profile.personal_info.city) score += 4;

  // Bonus premium (15 points)
  if (config.user_tier === 'premium') score += 15;

  return Math.min(100, score);
}

function generateCVHTML(profile, templateId) {
  const templates = {
    'modern': generateModernTemplate(profile),
    'ats_optimized': generateATSTemplate(profile),
    'creative': generateCreativeTemplate(profile)
  };

  return templates[templateId] || templates.modern;
}

function generateModernTemplate(profile) {
  const experiencesHTML = profile.experiences.map(exp => `
    <div class="experience-item">
      <h3>${exp.position}</h3>
      <p class="company">${exp.company} ‚Ä¢ ${exp.start_date} - ${exp.end_date || 'Pr√©sent'}</p>
      <p class="description">${exp.description || ''}</p>
    </div>
  `).join('');

  const skillsHTML = profile.skills.map(skill => `
    <span class="skill-tag">${skill.name || skill}</span>
  `).join('');

  return `<!DOCTYPE html>
<html>
<head>
    <title>CV - ${profile.personal_info.first_name} ${profile.personal_info.last_name}</title>
    <meta charset="UTF-8">
    <style>
        body { font-family: 'Arial', sans-serif; line-height: 1.6; color: #333; max-width: 800px; margin: 0 auto; padding: 20px; }
        .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; border-radius: 15px; text-align: center; margin-bottom: 30px; }
        .header h1 { margin: 0; font-size: 2.5em; }
        .header p { margin: 5px 0; opacity: 0.9; }
        .section { margin-bottom: 25px; }
        .section h2 { color: #667eea; border-bottom: 2px solid #667eea; padding-bottom: 5px; }
        .experience-item { margin-bottom: 20px; padding: 15px; background: #f8f9fa; border-radius: 10px; }
        .experience-item h3 { margin: 0 0 5px 0; color: #333; }
        .company { color: #666; font-weight: bold; margin: 0 0 10px 0; }
        .description { margin: 0; }
        .skill-tag { display: inline-block; background: linear-gradient(45deg, #ff7a00, #ff0040); color: white; padding: 5px 12px; margin: 3px; border-radius: 15px; font-size: 0.9em; }
        .contact-info { background: #e9ecef; padding: 15px; border-radius: 10px; }
        .summary { background: linear-gradient(45deg, #f8f9fa, #ffffff); padding: 20px; border-radius: 10px; border-left: 4px solid #667eea; }
    </style>
</head>
<body>
    <div class="header">
        <h1>${profile.personal_info.first_name} ${profile.personal_info.last_name}</h1>
        <p>üìß ${profile.personal_info.email}</p>
        ${profile.personal_info.phone ? `<p>üì± ${profile.personal_info.phone}</p>` : ''}
        ${profile.personal_info.city ? `<p>üìç ${profile.personal_info.city}</p>` : ''}
        ${profile.personal_info.linkedin_url ? `<p>üíº <a href="${profile.personal_info.linkedin_url}" style="color: white;">LinkedIn</a></p>` : ''}
    </div>

    ${profile.professional_summary ? `
    <div class="section">
        <h2>üéØ R√©sum√© Professionnel</h2>
        <div class="summary">
            <p>${profile.professional_summary}</p>
        </div>
    </div>
    ` : ''}

    ${profile.experiences.length > 0 ? `
    <div class="section">
        <h2>üíº Exp√©rience Professionnelle</h2>
        ${experiencesHTML}
    </div>
    ` : ''}

    ${profile.skills.length > 0 ? `
    <div class="section">
        <h2>üéØ Comp√©tences</h2>
        <div class="skills">
            ${skillsHTML}
        </div>
    </div>
    ` : ''}

    <div class="footer" style="text-align: center; margin-top: 40px; padding: 20px; background: #f8f9fa; border-radius: 10px; color: #666;">
        <p>üî• <strong>CV g√©n√©r√© par Phoenix</strong> ‚Ä¢ Sp√©cialis√© reconversions professionnelles</p>
        <p style="font-size: 0.9em;">phoenix-ecosystem.com</p>
    </div>
</body>
</html>`;
}

function generateATSTemplate(profile) {
  // Template ATS-optimized simplifi√©
  return generateModernTemplate(profile).replace(
    /<style>[\s\S]*?<\/style>/,
    '<style>body { font-family: Arial, sans-serif; line-height: 1.5; color: #000; } h1,h2,h3 { color: #000; } .skill-tag { display: inline; background: none; color: #000; padding: 0; margin-right: 10px; }</style>'
  );
}

function generateCreativeTemplate(profile) {
  // Template cr√©atif avec plus de couleurs
  return generateModernTemplate(profile).replace(
    'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    'linear-gradient(135deg, #ff7a00 0%, #ff0040 100%)'
  );
}

function calculateCO2Impact(userTier, generationTime, hasAISummary, hasATSAnalysis) {
  let baseImpact = 0.5; // Base CV generation
  if (hasAISummary) baseImpact += 0.3;
  if (hasATSAnalysis) baseImpact += 0.4;

  const timeMultiplier = generationTime / 1000 / 2; // Par tranche de 2 secondes
  const tierMultiplier = userTier === 'premium' ? 0.9 : 1.0;

  return Math.round((baseImpact * timeMultiplier * tierMultiplier) * 100) / 100;
}

function generateRecommendations(profile, atsAnalysis, config) {
  const recommendations = [];

  if (!profile.professional_summary) {
    recommendations.push('üí° Ajoutez un r√©sum√© professionnel accrocheur');
  }

  if (profile.experiences.some(exp => !exp.description || exp.description.length < 50)) {
    recommendations.push('üìù Enrichissez vos descriptions d\'exp√©rience');
  }

  if (profile.skills.length < 8) {
    recommendations.push('üéØ Ajoutez plus de comp√©tences pour vous d√©marquer');
  }

  if (atsAnalysis && atsAnalysis.recommendations) {
    recommendations.push(...atsAnalysis.recommendations.slice(0, 2));
  }

  return recommendations.slice(0, 5);
}

function getPremiumFeaturesUsed(hasAISummary, hasATSAnalysis) {
  const features = [];
  if (hasAISummary) features.push('AI Professional Summary');
  if (hasATSAnalysis) features.push('ATS Analysis & Optimization');
  return features;
}

function countCVSections(profile) {
  let count = 1; // Personal info always present
  if (profile.professional_summary) count++;
  if (profile.experiences.length > 0) count++;
  if (profile.education.length > 0) count++;
  if (profile.skills.length > 0) count++;
  if (profile.languages.length > 0) count++;
  if (profile.projects.length > 0) count++;
  return count;
}

function calculateTotalExperience(experiences) {
  // Calcul approximatif bas√© sur les dates
  return experiences.length * 2; // 2 ans en moyenne par exp√©rience
}

