// üî• PHOENIX LETTERS GENERATOR - Version N8N Pure
// G√©n√©ration de lettres de motivation IA sp√©cialis√©es reconversion

const inputData = $input.first().json;
const startTime = Date.now();

// ‚úÖ VALIDATION S√âCURIS√âE
if (!inputData.user_email || !inputData.cv_content || !inputData.job_offer) {
  throw new Error('Donn√©es manquantes: user_email, cv_content et job_offer requis');
}

if (inputData.cv_content.length < 100) {
  throw new Error('CV trop court (minimum 100 caract√®res)');
}

if (inputData.job_offer.length < 50) {
  throw new Error('Offre emploi trop courte (minimum 50 caract√®res)');
}

// üéØ CONFIGURATION PHOENIX
const config = {
  user_email: inputData.user_email,
  user_tier: inputData.user_tier || 'free',
  tone: inputData.tone || 'professional',
  company_name: inputData.company_name || 'l\'entreprise',
  reconversion_focus: inputData.reconversion_focus !== false, // true par d√©faut
  cv_content: inputData.cv_content.substring(0, 2000), // Limite s√©curit√©
  job_offer: inputData.job_offer.substring(0, 1500),
  session_id: `phoenix_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
};

// üé® PROMPTS PHOENIX LETTERS OPTIMIS√âS
const tonePrompts = {
  'professional': {
    style: 'R√©digez une lettre de motivation professionnelle, structur√©e et convaincante',
    personality: 's√©rieux et comp√©tent'
  },
  'dynamic': {
    style: 'Cr√©ez une lettre de motivation √©nergique et proactive', 
    personality: 'dynamique et motiv√©'
  },
  'creative': {
    style: 'Composez une lettre de motivation cr√©ative et originale',
    personality: 'innovant et cr√©atif'
  },
  'friendly': {
    style: 'R√©digez une lettre chaleureuse et accessible',
    personality: 'bienveillant et collaboratif'
  }
};

const selectedTone = tonePrompts[config.tone] || tonePrompts.professional;

// üöÄ PROMPT MASTER PHOENIX LETTERS
const phoenixPrompt = `${selectedTone.style} pour un candidat en reconversion professionnelle.

üìã PROFIL CANDIDAT (CV) :
${config.cv_content}

üéØ OFFRE D'EMPLOI CIBL√âE :
${config.job_offer}

üè¢ ENTREPRISE : ${config.company_name}

‚ö° CONSIGNES PHOENIX SP√âCIALIS√âES RECONVERSION :

1. üîÑ FOCUS RECONVERSION :
   - Mettre en avant la MOTIVATION du changement professionnel
   - Valoriser les COMP√âTENCES TRANSF√âRABLES
   - Pr√©senter la reconversion comme une FORCE, pas une faiblesse
   - Montrer la COH√âRENCE du projet professionnel

2. üìù STRUCTURE OPTIMALE :
   - Introduction : Accroche sur la reconversion motiv√©e
   - D√©veloppement : Comp√©tences transf√©rables + exp√©riences pertinentes
   - Conclusion : Vision future + demande d'entretien

3. üéØ STYLE & TON :
   - Personnalit√© : ${selectedTone.personality}
   - √âviter les formules bateau
   - Phrases courtes et impactantes
   - Vocabulaire du secteur vis√©

4. üìè CONTRAINTES :
   - Maximum 350 mots
   - 3 paragraphes principaux
   - Commencer par "Madame, Monsieur,"
   - Terminer par "Cordialement,"

üî• OBJECTIF PHOENIX : Transformer cette reconversion en atout d√©cisif !

Lettre de motivation :`;

// üìä PR√âPARATION DONN√âES POUR GEMINI
const geminiPayload = {
  prompt: phoenixPrompt,
  config: config,
  metadata: {
    generation_start: startTime,
    phoenix_version: '2.0.0',
    specialization: 'reconversion_professionnelle'
  }
};

return { json: geminiPayload };

// üîç PHOENIX LETTERS - Post-traitement & Qualit√©

const geminiResponse = $input.first().json;
const promptData = $('Phoenix Prompt Builder').first().json;
const generationTime = Date.now() - promptData.metadata.generation_start;

// ‚úÖ EXTRACTION S√âCURIS√âE DU CONTENU
let letterContent = '';
try {
  letterContent = geminiResponse.candidates[0]?.content?.parts[0]?.text || '';
} catch (error) {
  throw new Error(`Erreur extraction Gemini: ${error.message}`);
}

if (!letterContent || letterContent.length < 100) {
  throw new Error('G√©n√©ration Gemini √©chou√©e - contenu insuffisant');
}

// üîß NETTOYAGE & FORMATAGE PHOENIX
let cleanedLetter = letterContent
  .replace(/\*\*([^*]+)\*\*/g, '$1') // Supprime **bold**
  .replace(/\*([^*]+)\*/g, '$1')     // Supprime *italique*
  .replace(/#{1,6}\s/g, '')          // Supprime # headers
  .replace(/\n{3,}/g, '\n\n')        // Max 2 retours ligne cons√©cutifs
  .trim();

// üéØ ANALYSE QUALIT√â PHOENIX
const qualityAnalysis = analyzeLetterQuality(cleanedLetter, promptData.config);

// üå± CALCUL IMPACT CO2 (GREEN AI)
const co2Impact = calculateGreenImpact(promptData.config.user_tier, generationTime);

// üìä SUGGESTIONS PHOENIX (si premium)
const suggestions = [];
if (promptData.config.user_tier === 'premium') {
  suggestions.push(...generatePremiumSuggestions(cleanedLetter, promptData.config));
}

// üìÑ R√âSULTAT FINAL PHOENIX
const phoenixResult = {
  success: true,
  letter_id: `phoenix_letter_${Date.now()}_${Math.random().toString(36).substr(2, 8)}`,
  letter_content: cleanedLetter,
  user_email: promptData.config.user_email,
  user_tier: promptData.config.user_tier,
  generation_time_ms: generationTime,
  generation_time_s: Math.round(generationTime / 1000 * 100) / 100,
  quality_score: qualityAnalysis.score,
  co2_impact_grams: co2Impact,
  suggestions: suggestions,
  metadata: {
    tone_used: promptData.config.tone,
    company_name: promptData.config.company_name,
    reconversion_focus: promptData.config.reconversion_focus,
    word_count: cleanedLetter.split(' ').length,
    session_id: promptData.config.session_id,
    phoenix_version: '2.0.0',
    timestamp: new Date().toISOString()
  }
};

return { json: phoenixResult };

// üõ†Ô∏è FONCTIONS UTILITAIRES PHOENIX

function analyzeLetterQuality(letter, config) {
  let score = 0;
  const analysis = {
    length_ok: false,
    structure_ok: false,
    reconversion_focus: false,
    company_mentioned: false,
    tone_appropriate: false
  };

  // Longueur (20 points)
  const wordCount = letter.split(' ').length;
  if (wordCount >= 200 && wordCount <= 400) {
    score += 20;
    analysis.length_ok = true;
  }

  // Structure (25 points)
  if (letter.includes('Madame, Monsieur') && letter.includes('Cordialement')) {
    score += 25;
    analysis.structure_ok = true;
  }

  // Focus reconversion (25 points)
  const reconversionKeywords = ['reconversion', 'changement', 'transition', '√©volution', 'nouvelle', 'exp√©rience'];
  const hasReconversionFocus = reconversionKeywords.some(keyword => 
    letter.toLowerCase().includes(keyword)
  );
  if (hasReconversionFocus) {
    score += 25;
    analysis.reconversion_focus = true;
  }

  // Entreprise mentionn√©e (15 points)
  if (config.company_name !== 'l\'entreprise' && 
      letter.toLowerCase().includes(config.company_name.toLowerCase())) {
    score += 15;
    analysis.company_mentioned = true;
  }

  // Ton appropri√© (15 points)
  score += 15; // Par d√©faut Gemini respecte le ton
  analysis.tone_appropriate = true;

  return {
    score: Math.min(100, score),
    details: analysis
  };
}

function calculateGreenImpact(userTier, generationTime) {
  // Calcul impact CO2 bas√© sur la consommation IA
  const baseImpact = 1.2; // grammes CO2 base
  const timeMultiplier = generationTime / 1000 / 3; // Par tranche de 3 secondes
  const tierMultiplier = userTier === 'premium' ? 0.8 : 1.0; // Premium plus efficace

  return Math.round((baseImpact * timeMultiplier * tierMultiplier) * 100) / 100;
}

function generatePremiumSuggestions(letter, config) {
  const suggestions = [];

  // Analyse longueur
  const wordCount = letter.split(' ').length;
  if (wordCount < 250) {
    suggestions.push('üí° D√©veloppez davantage vos comp√©tences transf√©rables');
  }

  // Analyse mots-cl√©s du job
  const jobKeywords = extractKeywords(config.job_offer);
  const letterKeywords = extractKeywords(letter);
  const missingKeywords = jobKeywords.filter(kw => !letterKeywords.includes(kw));

  if (missingKeywords.length > 0) {
    suggestions.push(`üéØ Int√©grez ces mots-cl√©s: ${missingKeywords.slice(0, 3).join(', ')}`);
  }

  // Suggestion personnalisation
  if (!letter.toLowerCase().includes('projet') && !letter.toLowerCase().includes('objectif')) {
    suggestions.push('üöÄ Ajoutez une mention de vos projets futurs dans l\'entreprise');
  }

  return suggestions;
}

function extractKeywords(text) {
  // Extraction simplifi√©e de mots-cl√©s
  return text.toLowerCase()
    .split(/\s+/)
    .filter(word => word.length > 4)
    .filter(word => !/^(dans|avec|pour|cette|vous|nous|que|qui|une|des|les|est)/.test(word))
    .slice(0, 10);
}

