// üìä PHOENIX QUOTA - V√©rification des limites

const freeQuota = 2; // Exemple: 2 lettres gratuites par mois
const premiumQuota = 99999; // Exemple: Quota illimit√© pour premium

const usersData = $input.all();
const usersToAlert = [];

for (const userItem of usersData) {
  const userId = userItem.json.user_id;
  const userTier = userItem.json.user_tier;
  const generationCount = userItem.json.count; // Assumant que le n≈ìud Supabase renvoie un 'count'

  let quotaExceeded = false;
  if (userTier === 'free' && generationCount > freeQuota) {
    quotaExceeded = true;
  } else if (userTier === 'premium' && generationCount > premiumQuota) {
    quotaExceeded = true; // Juste au cas o√π on voudrait limiter les premium un jour
  }

  if (quotaExceeded) {
    usersToAlert.push({
      json: {
        user_id: userId,
        user_tier: userTier,
        generation_count: generationCount,
        quota_limit: userTier === 'free' ? freeQuota : premiumQuota,
        alert_type: 'quota_exceeded'
      }
    });
  }
}

return usersToAlert;
