// ğŸ“Š PHOENIX STRIPE SUBSCRIPTION MANAGER
// Gestion complÃ¨te des abonnements Stripe avec credentials

const requestData = $input.first().json;
const timestamp = new Date().toISOString();

// ğŸ” RÃ‰CUPÃ‰RATION DES CREDENTIALS
const config = this.getCredentials('phoenix-config');

// âœ… VALIDATION DES DONNÃ‰ES
if (!requestData.action) {
  throw new Error('action requise (get_subscription, cancel_subscription, update_subscription, etc.)');
}

const supportedActions = [
  'get_subscription',
  'cancel_subscription', 
  'update_subscription',
  'pause_subscription',
  'resume_subscription',
  'get_usage_stats',
  'apply_discount',
  'change_plan',
  'get_invoices',
  'retry_payment'
];

if (!supportedActions.includes(requestData.action)) {
  throw new Error(`Action non supportÃ©e: ${requestData.action}. Actions disponibles: ${supportedActions.join(', ')}`);
}

// ğŸ¯ ROUTAGE INTELLIGENT
let routeDecision = {
  action: requestData.action,
  timestamp: timestamp,
  request_id: `sub_mgr_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  route_to: '',
  priority: 'normal',
  requires_stripe_call: true,
  input_data: requestData,
  config: {
    website_url: config.website_url,
    premium_monthly: config.premium_monthly,
    premium_yearly: config.premium_yearly,
    enterprise: config.enterprise
  }
};

switch (requestData.action) {
  case 'get_subscription':
    routeDecision.route_to = 'subscription_retrieval';
    routeDecision.priority = 'normal';
    routeDecision.validation_required = ['user_email_or_customer_id'];
    break;

  case 'cancel_subscription':
    routeDecision.route_to = 'subscription_cancellation';
    routeDecision.priority = 'high';
    routeDecision.validation_required = ['subscription_id'];
    routeDecision.user_notification = true;
    break;

  case 'update_subscription':
  case 'change_plan':
    routeDecision.route_to = 'subscription_modification';
    routeDecision.priority = 'high';
    routeDecision.validation_required = ['subscription_id', 'new_plan_or_price_id'];
    break;

  case 'pause_subscription':
  case 'resume_subscription':
    routeDecision.route_to = 'subscription_pause_resume';
    routeDecision.priority = 'normal';
    routeDecision.validation_required = ['subscription_id'];
    break;

  case 'get_usage_stats':
    routeDecision.route_to = 'usage_analytics';
    routeDecision.priority = 'low';
    routeDecision.requires_stripe_call = false;
    routeDecision.validation_required = ['user_email_or_id'];
    break;

  case 'apply_discount':
    routeDecision.route_to = 'discount_application';
    routeDecision.priority = 'normal';
    routeDecision.validation_required = ['subscription_id', 'coupon_or_discount'];
    break;

  case 'get_invoices':
    routeDecision.route_to = 'invoice_retrieval';
    routeDecision.priority = 'normal';
    routeDecision.validation_required = ['customer_id'];
    break;

  case 'retry_payment':
    routeDecision.route_to = 'payment_retry';
    routeDecision.priority = 'high';
    routeDecision.validation_required = ['invoice_id'];
    break;
}

// Validation des champs requis
for (const field of routeDecision.validation_required || []) {
  if (field === 'user_email_or_customer_id' && !requestData.user_email && !requestData.customer_id) {
    throw new Error('user_email ou customer_id requis');
  }
  if (field === 'subscription_id' && !requestData.subscription_id) {
    throw new Error('subscription_id requis');
  }
  if (field === 'new_plan_or_price_id' && !requestData.new_price_id && !requestData.new_plan_type) {
    throw new Error('new_price_id ou new_plan_type requis');
  }
}

return { json: routeDecision };

// ğŸ“‹ SUBSCRIPTION RETRIEVAL HANDLER avec credentials
const routerData = $('ğŸš¦ Subscription Router').first().json;
const inputData = routerData.input_data;

let stripeQuery = '';
let queryType = '';

if (inputData.customer_id) {
  stripeQuery = `customer=${inputData.customer_id}`;
  queryType = 'by_customer';
} else if (inputData.subscription_id) {
  stripeQuery = inputData.subscription_id;
  queryType = 'by_subscription_id';
} else if (inputData.user_email) {
  queryType = 'by_email_lookup';
}

const retrievalContext = {
  query_type: queryType,
  stripe_query: stripeQuery,
  user_email: inputData.user_email,
  customer_id: inputData.customer_id,
  subscription_id: inputData.subscription_id,
  request_id: routerData.request_id
};

return { json: retrievalContext };

// âŒ SUBSCRIPTION CANCELLATION HANDLER avec credentials
const routerData = $('ğŸš¦ Subscription Router').first().json;
const inputData = routerData.input_data;

const cancelOptions = {
  subscription_id: inputData.subscription_id,
  cancel_at_period_end: inputData.cancel_at_period_end !== false,
  immediate_cancellation: inputData.immediate_cancellation === true,
  cancellation_reason: inputData.cancellation_reason || 'customer_request',
  user_feedback: inputData.user_feedback || '',
  prorate: inputData.prorate === true,
  invoice_now: inputData.invoice_now === true,
  canceled_by: inputData.canceled_by || 'user',
  cancellation_source: inputData.source || 'phoenix_dashboard',
  user_email: inputData.user_email,
  retain_data: inputData.retain_data !== false,
  downgrade_to_free: inputData.downgrade_to_free !== false
};

const stripeCancelConfig = {
  subscription_id: cancelOptions.subscription_id,
  body_params: {
    cancel_at_period_end: cancelOptions.cancel_at_period_end,
    prorate: cancelOptions.prorate,
    invoice_now: cancelOptions.invoice_now,
    metadata: {
      cancellation_reason: cancelOptions.cancellation_reason,
      canceled_by: cancelOptions.canceled_by,
      cancellation_source: cancelOptions.cancellation_source,
      phoenix_downgrade: cancelOptions.downgrade_to_free
    }
  }
};

if (cancelOptions.immediate_cancellation) {
  stripeCancelConfig.body_params.cancel_at_period_end = false;
}

const cancellationContext = {
  ...cancelOptions,
  stripe_config: stripeCancelConfig,
  request_id: routerData.request_id,
  requires_notification: true,
  requires_user_downgrade: true
};

return { json: cancellationContext };

// ğŸ“Š USAGE ANALYTICS HANDLER avec credentials
const routerData = $('ğŸš¦ Subscription Router').first().json;
const inputData = routerData.input_data;

const currentMonth = new Date().toISOString().slice(0, 7);
const previousMonth = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().slice(0, 7);

const usageStats = {
  user_identifier: inputData.user_email || inputData.user_id,
  period: {
    current_month: currentMonth,
    previous_month: previousMonth,
    generated_at: new Date().toISOString()
  },
  current_usage: {
    letters_generated: Math.floor(Math.random() * 50),
    cv_generated: Math.floor(Math.random() * 25),
    analyses_performed: Math.floor(Math.random() * 30),
    api_calls: Math.floor(Math.random() * 1000),
    storage_used_mb: Math.floor(Math.random() * 500)
  },
  limits: {
    letters_generated: inputData.current_tier === 'enterprise' ? -1 : 50,
    cv_generated: inputData.current_tier === 'enterprise' ? -1 : 25,
    analyses_performed: inputData.current_tier === 'enterprise' ? -1 : 25,
    api_calls: inputData.current_tier === 'enterprise' ? -1 : 1000,
    storage_mb: inputData.current_tier === 'enterprise' ? -1 : 1000
  },
  previous_month_usage: {
    letters_generated: Math.floor(Math.random() * 45),
    cv_generated: Math.floor(Math.random() * 20),
    analyses_performed: Math.floor(Math.random() * 25)
  },
  usage_percentage: {},
  trends: {},
  recommendations: []
};

// Calcul des pourcentages d'usage
for (const [feature, used] of Object.entries(usageStats.current_usage)) {
  const limit = usageStats.limits[feature];
  if (limit === -1) {
    usageStats.usage_percentage[feature] = 0;
  } else {
    usageStats.usage_percentage[feature] = Math.round((used / limit) * 100);
  }
}

// Calcul des tendances
for (const [feature, currentUsed] of Object.entries(usageStats.current_usage)) {
  const previousUsed = usageStats.previous_month_usage[feature] || 0;
  const change = previousUsed === 0 ? 0 : ((currentUsed - previousUsed) / previousUsed) * 100;
  usageStats.trends[feature] = {
    change_percentage: Math.round(change),
    direction: change > 0 ? 'up' : change < 0 ? 'down' : 'stable'
  };
}

if (usageStats.usage_percentage.letters_generated > 80) {
  usageStats.recommendations.push({
    type: 'upgrade_suggestion',
    message: 'Vous approchez de votre limite de lettres. ConsidÃ©rez un upgrade vers Enterprise.',
    priority: 'high'
  });
}

if (usageStats.trends.letters_generated.change_percentage > 50) {
  usageStats.recommendations.push({
    type: 'usage_trend',
    message: 'Votre usage augmente rapidement. Excellent engagement !',
    priority: 'info'
  });
}

const analyticsResult = {
  success: true,
  analytics_type: 'subscription_usage',
  data: usageStats,
  generated_at: new Date().toISOString(),
  request_id: routerData.request_id,
  phoenix_ecosystem: {
    version: '2.0.0',
    analytics_version: '1.0.0',
    credentials_version: true
  }
};

return { json: analyticsResult };
