// ğŸ’³ PHOENIX STRIPE WEBHOOKS - ROUTER COMPLET avec credentials
const stripeEvent = $input.first().json;
const timestamp = new Date().toISOString();

// ğŸ” RÃ‰CUPÃ‰RATION DES CREDENTIALS
const config = this.getCredentials('phoenix-config');

// Validation Ã©vÃ©nement Stripe
if (!stripeEvent.type || !stripeEvent.data || !stripeEvent.data.object) {
  throw new Error('Ã‰vÃ©nement Stripe invalide');
}

const eventType = stripeEvent.type;
const eventObject = stripeEvent.data.object;
const eventId = stripeEvent.id;

// ğŸ¯ CLASSIFICATION DES Ã‰VÃ‰NEMENTS STRIPE
const eventCategories = {
  'checkout.session.completed': 'payment_success',
  'checkout.session.expired': 'payment_expired',
  'customer.subscription.created': 'subscription_created',
  'customer.subscription.updated': 'subscription_updated',
  'customer.subscription.deleted': 'subscription_canceled',
  'customer.subscription.trial_will_end': 'trial_ending',
  'invoice.payment_succeeded': 'payment_succeeded',
  'invoice.payment_failed': 'payment_failed',
  'invoice.upcoming': 'invoice_upcoming',
  'invoice.finalized': 'invoice_finalized',
  'customer.created': 'customer_created',
  'customer.updated': 'customer_updated',
  'customer.deleted': 'customer_deleted',
  'payment_method.attached': 'payment_method_added',
  'payment_method.detached': 'payment_method_removed',
  'charge.dispute.created': 'dispute_created',
  'charge.refunded': 'refund_processed'
};

const eventCategory = eventCategories[eventType] || 'unknown_event';

// ğŸš¦ ROUTAGE INTELLIGENT
let routeDecision = {
  event_id: eventId,
  event_type: eventType,
  event_category: eventCategory,
  timestamp: timestamp,
  route_to: '',
  priority: 'normal',
  requires_user_update: false,
  requires_notification: false,
  stripe_data: eventObject,
  config: {
    website_url: config.website_url,
    premium_monthly: config.premium_monthly,
    premium_yearly: config.premium_yearly,
    enterprise: config.enterprise
  }
};

// Extraction donnÃ©es utilisateur selon le type d'Ã©vÃ©nement
let userEmail = '';
let customerId = '';
let subscriptionId = '';
let amountCents = 0;

switch (eventType) {
  case 'checkout.session.completed':
    userEmail = eventObject.customer_details?.email || eventObject.customer_email;
    customerId = eventObject.customer;
    subscriptionId = eventObject.subscription;
    amountCents = eventObject.amount_total;
    routeDecision.route_to = 'payment_success_handler';
    routeDecision.priority = 'high';
    routeDecision.requires_user_update = true;
    routeDecision.requires_notification = true;
    break;

  case 'checkout.session.expired':
    userEmail = eventObject.customer_details?.email;
    routeDecision.route_to = 'payment_expired_handler';
    routeDecision.requires_notification = true;
    break;

  case 'customer.subscription.created':
  case 'customer.subscription.updated':
    customerId = eventObject.customer;
    subscriptionId = eventObject.id;
    routeDecision.route_to = 'subscription_change_handler';
    routeDecision.priority = 'high';
    routeDecision.requires_user_update = true;
    break;

  case 'customer.subscription.deleted':
    customerId = eventObject.customer;
    subscriptionId = eventObject.id;
    routeDecision.route_to = 'subscription_canceled_handler';
    routeDecision.priority = 'high';
    routeDecision.requires_user_update = true;
    routeDecision.requires_notification = true;
    break;

  case 'invoice.payment_succeeded':
    customerId = eventObject.customer;
    subscriptionId = eventObject.subscription;
    amountCents = eventObject.amount_paid;
    routeDecision.route_to = 'payment_succeeded_handler';
    routeDecision.requires_notification = true;
    break;

  case 'invoice.payment_failed':
    customerId = eventObject.customer;
    subscriptionId = eventObject.subscription;
    amountCents = eventObject.amount_due;
    routeDecision.route_to = 'payment_failed_handler';
    routeDecision.priority = 'high';
    routeDecision.requires_notification = true;
    break;

  case 'customer.subscription.trial_will_end':
    customerId = eventObject.customer;
    subscriptionId = eventObject.id;
    routeDecision.route_to = 'trial_ending_handler';
    routeDecision.requires_notification = true;
    break;

  default:
    routeDecision.route_to = 'event_logger';
    routeDecision.priority = 'low';
}

// Enrichissement des donnÃ©es
routeDecision.user_context = {
  email: userEmail,
  customer_id: customerId,
  subscription_id: subscriptionId,
  amount_cents: amountCents,
  currency: eventObject.currency || 'eur'
};

// MÃ©tadonnÃ©es Phoenix
routeDecision.phoenix_context = {
  ecosystem_version: '2.0.0',
  stripe_webhook_version: '2.0.0',
  processing_node: 'stripe_webhook_router',
  requires_sso_sync: routeDecision.requires_user_update,
  credentials_version: true
};

return { json: routeDecision };

// âœ… PAYMENT SUCCESS HANDLER avec credentials
const routeData = $('ğŸš¦ Stripe Webhook Router').first().json;
const stripeData = routeData.stripe_data;
const userContext = routeData.user_context;
const config = routeData.config;

// DÃ©termination du tier selon le price_id ou amount
let newTier = 'premium';
let planType = 'monthly';

// Logique de dÃ©termination du plan avec credentials
if (stripeData.amount_total) {
  const amountEur = stripeData.amount_total / 100;
  if (amountEur >= 200) {
    newTier = 'enterprise';
    planType = 'yearly';
  } else if (amountEur >= 100) {
    newTier = 'premium';
    planType = 'yearly';
  } else {
    newTier = 'premium';
    planType = 'monthly';
  }
}

// VÃ©rification avec price_id depuis credentials
if (stripeData.subscription && stripeData.subscription.items) {
  const items = stripeData.subscription.items.data || [];
  for (const item of items) {
    const priceId = item.price?.id;
    if (priceId === config.enterprise) {
      newTier = 'enterprise';
      planType = 'yearly';
    } else if (priceId === config.premium_yearly) {
      newTier = 'premium';
      planType = 'yearly';
    } else if (priceId === config.premium_monthly) {
      newTier = 'premium';
      planType = 'monthly';
    }
  }
}

const upgradeData = {
  user_email: userContext.email,
  customer_id: userContext.customer_id,
  subscription_id: userContext.subscription_id,
  new_tier: newTier,
  plan_type: planType,
  amount_paid: userContext.amount_cents / 100,
  currency: userContext.currency,
  upgrade_source: 'stripe_checkout',
  upgrade_timestamp: new Date().toISOString(),
  stripe_session_id: stripeData.id,
  unlocked_features: {
    premium_templates: true,
    unlimited_generations: newTier === 'enterprise',
    priority_support: true,
    advanced_analytics: newTier === 'enterprise',
    api_access: newTier === 'enterprise'
  }
};

return { json: upgradeData };

// âŒ PAYMENT FAILED HANDLER avec credentials
const routeData = $('ğŸš¦ Stripe Webhook Router').first().json;
const stripeData = routeData.stripe_data;
const userContext = routeData.user_context;

let failureReason = 'unknown';
let actionRequired = 'contact_user';
let severity = 'medium';

if (stripeData.last_payment_error) {
  const errorCode = stripeData.last_payment_error.code;

  switch (errorCode) {
    case 'card_declined':
      failureReason = 'card_declined';
      actionRequired = 'update_payment_method';
      severity = 'high';
      break;
    case 'insufficient_funds':
      failureReason = 'insufficient_funds';
      actionRequired = 'retry_payment';
      severity = 'high';
      break;
    case 'expired_card':
      failureReason = 'expired_card';
      actionRequired = 'update_payment_method';
      severity = 'high';
      break;
    default:
      failureReason = errorCode || 'generic_decline';
      actionRequired = 'contact_support';
  }
}

const failureData = {
  user_email: userContext.email,
  customer_id: userContext.customer_id,
  subscription_id: userContext.subscription_id,
  failure_reason: failureReason,
  action_required: actionRequired,
  severity: severity,
  amount_failed: userContext.amount_cents / 100,
  currency: userContext.currency,
  retry_attempts: stripeData.attempt_count || 1,
  next_retry_date: stripeData.next_payment_attempt,
  dunning_stage: 'initial_failure',
  send_failure_email: true,
  suspend_premium_features: severity === 'high',
  grace_period_days: severity === 'high' ? 3 : 7,
  failure_timestamp: new Date().toISOString(),
  stripe_invoice_id: stripeData.id,
  error_details: stripeData.last_payment_error
};

return { json: failureData };

// ğŸ”„ SUBSCRIPTION CHANGE HANDLER avec credentials
const routeData = $('ğŸš¦ Stripe Webhook Router').first().json;
const stripeData = routeData.stripe_data;
const userContext = routeData.user_context;
const eventType = routeData.event_type;
const config = routeData.config;

let changeType = 'unknown';
let newStatus = stripeData.status;
let newTier = 'free';

switch (eventType) {
  case 'customer.subscription.created':
    changeType = 'subscription_created';
    newTier = 'premium';
    break;
  case 'customer.subscription.updated':
    changeType = 'subscription_updated';
    // DÃ©termination du tier avec credentials
    if (stripeData.items && stripeData.items.data.length > 0) {
      const priceId = stripeData.items.data[0].price.id;
      if (priceId === config.enterprise) {
        newTier = 'enterprise';
      } else if (priceId === config.premium_yearly || priceId === config.premium_monthly) {
        newTier = 'premium';
      }
    }
    break;
  case 'customer.subscription.deleted':
    changeType = 'subscription_canceled';
    newTier = 'free';
    break;
}

const subscriptionChange = {
  customer_id: userContext.customer_id,
  subscription_id: userContext.subscription_id,
  change_type: changeType,
  old_status: stripeData.previous_attributes?.status || 'unknown',
  new_status: newStatus,
  new_tier: newTier,
  current_period_start: new Date(stripeData.current_period_start * 1000).toISOString(),
  current_period_end: new Date(stripeData.current_period_end * 1000).toISOString(),
  trial_end: stripeData.trial_end ? new Date(stripeData.trial_end * 1000).toISOString() : null,
  cancel_at_period_end: stripeData.cancel_at_period_end || false,
  canceled_at: stripeData.canceled_at ? new Date(stripeData.canceled_at * 1000).toISOString() : null,
  change_timestamp: new Date().toISOString(),
  stripe_subscription_data: stripeData,
  update_user_permissions: true,
  send_change_notification: changeType !== 'subscription_updated',
  sync_across_apps: true
};

return { json: subscriptionChange };

